export declare const TEMPLATE = "\n  <h1>\n    <span>Font Picker</span>\n    <span class=\"toggle-label\">Enabled</span>\n    <astro-dev-toolbar-toggle id=\"toggle\"></astro-dev-toolbar-toggle>\n    <span class=\"toggle-label\">Override All Styles</span>\n    <astro-dev-toolbar-toggle id=\"override\"></astro-dev-toolbar-toggle>\n  </h1>\n  <div class=\"flex\">\n    <select id=\"group-select\"></select>\n    <select id=\"font-select\"></select>    \n    <a id=\"google-link\" href=\"\" target=\"_blank\">View in Google Fonts</a>\n  </div>\n  <div class=\"instr\">\n    <div id=\"instr1\"></div>\n    <br/>\n    <div id=\"instr2\"></div>\n  </div>\n  \n  <style>\n    \n    h1 {\n        margin: 0;\n        display: inline-block;\n      }\n      \n      .toggle-label {\n        margin-left: 20px;\n        font-size: 14px;\n      }\n      \n      .flex {\n        display: flex;\n        align-items: baseline;\n      }\n      \n      select {\n        margin-top: 10px;\n        padding: 10px;\n        border-radius: 5px;\n        border: none;\n        background-color: #222;\n        color: #eee;\n      }\n      \n      select:focus-visible {\n        outline: 1px solid #888;\n      }\n      \n      select + select {\n        margin-left: 10px;\n      }\n      \n      select option {\n        background-color: #333;\n        color: #eee;\n        padding: 5px 5px;\n      }\n      \n      .instr {\n        margin-top: 10px;\n        border-radius: 5px;\n        overflow: hidden;\n        padding: 10px;\n        background-color: #333;\n        font-size: 12px;\n        font-family: monospace;\n      }\n      \n      a {\n        color: inherit;\n        margin-left: 20px;\n      }\n</style>\n";
